package com.firstems.erp.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.CheckBox;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.constraintlayout.widget.ConstraintLayout;import androidx.recyclerview.widget.RecyclerView;import com.firstems.erp.R;import com.firstems.erp.api.model.response.doi_tuong_nhan.DoiTuongNhanItem;import java.util.List;/** * Created by Nguyen Quoc Cuong on 9/4/2020. */public class DoiTuongNhanAdapter extends RecyclerView.Adapter<DoiTuongNhanAdapter.ViewHolder> {    private List<DoiTuongNhanItem>list;    public interface DoiTuongOnItemCheckListener{        void onItemClick(int newPosition, DoiTuongNhanItem item);    }    private DoiTuongOnItemCheckListener doiTuongOnItemCheckListener;        public void setDoiTuongOnItemCheckListener(DoiTuongOnItemCheckListener doiTuongOnItemCheckListener) {        this.doiTuongOnItemCheckListener = doiTuongOnItemCheckListener;    }        public DoiTuongNhanAdapter(List<DoiTuongNhanItem> list) {        this.list = list;    }    @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        LayoutInflater layoutInflater = LayoutInflater.from(parent.getContext());        View view = layoutInflater.inflate(R.layout.item_check, parent, false);        return new ViewHolder(view);    }        @Override    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {        DoiTuongNhanItem doiTuongNhanItem = list.get(position);        holder.txtContent.setText(doiTuongNhanItem.getiTEMNAME());        holder.chk.setChecked(doiTuongNhanItem.isCheck());        holder.layout.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                doiTuongOnItemCheckListener.onItemClick(position, doiTuongNhanItem);            }        });    }        @Override    public int getItemCount() {        return list.size();    }        class ViewHolder extends RecyclerView.ViewHolder{        TextView txtContent;        CheckBox chk;        ConstraintLayout layout;        public ViewHolder(@NonNull View itemView) {            super(itemView);            txtContent=itemView.findViewById(R.id.txtContent);            chk=itemView.findViewById(R.id.chk);            layout = itemView.findViewById(R.id.layoutMain);        }    }}