package com.firstems.erp.adapter;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.CheckBox;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.constraintlayout.widget.ConstraintLayout;import androidx.recyclerview.widget.RecyclerView;import com.firstems.erp.R;import com.firstems.erp.api.model.response.phieu_tam_ung.PhieuTamUngApiResponse;import com.firstems.erp.api.model.response.phieu_tam_ung.PhieuTamUngItem;import java.util.List;public class PhieuTamUngNeuCoAdapter extends RecyclerView.Adapter<PhieuTamUngNeuCoAdapter.ViewHolder>{    private List<PhieuTamUngItem> phieuTamUngItems;        public PhieuTamUngNeuCoAdapter(List<PhieuTamUngItem> phieuTamUngItems) {        this.phieuTamUngItems = phieuTamUngItems;    }    public interface PhieuTamUngOnClickListener{        void onItemCheck(int position, PhieuTamUngItem phieuTamUngItem);    }    private PhieuTamUngOnClickListener phieuTamUngOnClickListener;        public void setPhieuTamUngOnClickListener(PhieuTamUngOnClickListener phieuTamUngOnClickListener) {        this.phieuTamUngOnClickListener = phieuTamUngOnClickListener;    }        @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        LayoutInflater layoutInflater = LayoutInflater.from(parent.getContext());        View view  = layoutInflater.inflate(R.layout.item_check, parent,false);        return new ViewHolder(view);    }        @Override    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {        PhieuTamUngItem phieuTamUngItem = phieuTamUngItems.get(position);        holder.txtContent.setText(phieuTamUngItem.getiTEMNAME());        holder.chk.setChecked(phieuTamUngItem.isCheck());        holder.layoutMain.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                holder.chk.setChecked(!holder.chk.isChecked());                phieuTamUngOnClickListener.onItemCheck(position,phieuTamUngItem);            }        });    }        @Override    public int getItemCount() {        return phieuTamUngItems.size();    }        class ViewHolder extends RecyclerView.ViewHolder{        TextView txtContent;        CheckBox chk;        ConstraintLayout layoutMain;        public ViewHolder(@NonNull View itemView) {            super(itemView);            txtContent = itemView.findViewById(R.id.txtContent);            chk = itemView.findViewById(R.id.chk);            layoutMain = itemView.findViewById(R.id.layoutMain);        }    }}