package com.firstems.erp.adapter.company;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.firstems.erp.R;import com.firstems.erp.adapter.SelectLocationAdapter;import com.firstems.erp.api.model.response.company.CompanyResponse;import com.firstems.erp.api.model.response.location.LocationResponse;import com.skydoves.transformationlayout.TransformationLayout;import java.util.ArrayList;import java.util.List;public class CompanySelectAdapter extends RecyclerView.Adapter<CompanySelectAdapter.ViewHolder> {    private List<CompanyResponse> list;        public CompanySelectAdapter(List<CompanyResponse> list) {        this.list = list;    }    public interface SelectCompanyListener{        void onComapnyItemCick(CompanyResponse companyResponse, LocationResponse locationResponse);    }    private SelectCompanyListener selectCompanyListener;        public void setSelectCompanyListener(SelectCompanyListener selectCompanyListener) {        this.selectCompanyListener = selectCompanyListener;    }        @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        LayoutInflater layoutInflater = LayoutInflater.from(parent.getContext());        View view = layoutInflater.inflate(R.layout.item_company_select, parent, false);        return new ViewHolder(view);    }        @Override    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {        CompanyResponse companyResponse = list.get(position);        holder.txtCompName.setText(companyResponse.getCompanyName());        holder.locationResponses.clear();        holder.locationResponses.addAll(companyResponse.getLocationList());        holder.selectLocationAdapter.notifyDataSetChanged();        holder.itemView.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                holder.transformationLayout.startTransform();            }        });        holder.selectLocationAdapter.setDialogTopHomeOnClickListener(new SelectLocationAdapter.SelectLocationClickListener() {            @Override            public void onItemClick(LocationResponse model) {                holder.transformationLayout.finishTransform();                selectCompanyListener.onComapnyItemCick(companyResponse,model);            }        });    }        @Override    public int getItemCount() {        return list.size();    }        class ViewHolder extends RecyclerView.ViewHolder{        TextView txtCompName;        RecyclerView recyclerViewLocation;        List<LocationResponse>locationResponses;        SelectLocationAdapter selectLocationAdapter;        TransformationLayout transformationLayout;                public ViewHolder(@NonNull View itemView) {            super(itemView);            txtCompName = itemView.findViewById(R.id.name);            recyclerViewLocation = itemView.findViewById(R.id.recycleLocation);            locationResponses = new ArrayList<>();            selectLocationAdapter = new SelectLocationAdapter(locationResponses);            recyclerViewLocation.setAdapter(selectLocationAdapter);            transformationLayout = itemView.findViewById(R.id.transformationLayout);        }    }}