package com.firstems.erp.adapter.company;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.cardview.widget.CardView;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;import com.balysv.materialripple.MaterialRippleLayout;import com.firstems.erp.R;import com.firstems.erp.adapter.SelectLocationAdapter;import com.firstems.erp.api.model.response.company.CompanyResponse;import com.firstems.erp.api.model.response.location.LocationResponse;import com.skydoves.transformationlayout.TransformationLayout;import java.util.ArrayList;import java.util.List;public class CompanySelectAdapter extends RecyclerView.Adapter<CompanySelectAdapter.ViewHolder> {    private List<CompanyResponse> list;        public CompanySelectAdapter(List<CompanyResponse> list) {        this.list = list;    }    public interface SelectCompanyListener{        void onComapnyItemCick(CompanyResponse companyResponse);    }    private SelectCompanyListener selectCompanyListener;        public void setSelectCompanyListener(SelectCompanyListener selectCompanyListener) {        this.selectCompanyListener = selectCompanyListener;    }        @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        LayoutInflater layoutInflater = LayoutInflater.from(parent.getContext());        View view = layoutInflater.inflate(R.layout.item_company_select, parent, false);        return new ViewHolder(view);    }        @Override    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {        CompanyResponse companyResponse = list.get(position);        holder.txtCompName.setText(companyResponse.getCompanyName());        holder.materialRippleLayout.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                selectCompanyListener.onComapnyItemCick(companyResponse);            }        });    }        @Override    public int getItemCount() {        return list.size();    }        class ViewHolder extends RecyclerView.ViewHolder{        TextView txtCompName;        CardView cardViewParent;        MaterialRippleLayout materialRippleLayout;        public ViewHolder(@NonNull View itemView) {            super(itemView);            txtCompName = itemView.findViewById(R.id.name);            cardViewParent = itemView.findViewById(R.id.cardViewParent);            materialRippleLayout =  itemView.findViewById(R.id.materialRippleLayout);        }    }}