package com.firstems.erp.adapter.diff.approved;import android.os.Bundle;import androidx.annotation.Nullable;import androidx.recyclerview.widget.DiffUtil;import androidx.recyclerview.widget.ListUpdateCallback;import com.firstems.erp.api.model.response.approved.ApprovedItemApiResponse;import com.firstems.erp.common.Constant;import java.util.List;public class ApprovedDetailDiffUtilCallback extends DiffUtil.Callback {    private List<ApprovedItemApiResponse> newList ;    private List<ApprovedItemApiResponse> oldList;        public ApprovedDetailDiffUtilCallback(List<ApprovedItemApiResponse> newList, List<ApprovedItemApiResponse> oldList) {        this.newList = newList;        this.oldList = oldList;    }        @Override    public int getOldListSize() {        return oldList != null ? oldList.size() : 0;    }        @Override    public int getNewListSize() {        return newList != null ? newList.size() : 0;    }        @Override    public boolean areItemsTheSame(int oldItemPosition, int newItemPosition) {        return true;    }        @Override    public boolean areContentsTheSame(int oldItemPosition, int newItemPosition) {        ApprovedItemApiResponse newModel = newList.get(newItemPosition);        ApprovedItemApiResponse oldModel = oldList.get(oldItemPosition);        DiffUtil.DiffResult diffResult = DiffUtil.calculateDiff(new ApprovedDetail_1_DiffUtilCallback(newModel.getApprovedItemDetail_1List(),oldModel.getApprovedItemDetail_1List()));        final boolean[] flag = {true};        diffResult.dispatchUpdatesTo(new ListUpdateCallback() {            @Override            public void onInserted(int position, int count) {                flag[0] = false;            }                @Override            public void onRemoved(int position, int count) {                flag[0] = false;            }                @Override            public void onMoved(int fromPosition, int toPosition) {                flag[0] = false;            }                @Override            public void onChanged(int position, int count, @Nullable Object payload) {                flag[0] = false;            }        });        return flag[0];    }        @Nullable    @Override    public Object getChangePayload(int oldItemPosition, int newItemPosition) {        Bundle diff = new Bundle();            diff.putSerializable(Constant.NAME_PUT_DIFF_FLAG, "change");            if (diff.size() == 0) {            return null;        }            return diff;    }}