package com.firstems.erp.adapter.diff.approved;import android.os.Bundle;import androidx.annotation.Nullable;import androidx.recyclerview.widget.DiffUtil;import com.firstems.erp.api.model.response.approved.ApprovedItemDetail_2;import com.firstems.erp.common.Constant;import java.util.List;public class ApprovedDetail_2_DiffUtilCallback extends DiffUtil.Callback {    private List<ApprovedItemDetail_2> newList ;    private List<ApprovedItemDetail_2> oldList;        public ApprovedDetail_2_DiffUtilCallback(List<ApprovedItemDetail_2> newList, List<ApprovedItemDetail_2> oldList) {        this.newList = newList;        this.oldList = oldList;    }        @Override    public int getOldListSize() {        return oldList != null ? oldList.size() : 0;    }        @Override    public int getNewListSize() {        return newList != null ? newList.size() : 0;    }        @Override    public boolean areItemsTheSame(int oldItemPosition, int newItemPosition) {        return newList.get(newItemPosition).getkEYCODE().equals(oldList.get(oldItemPosition).getkEYCODE());    }        @Override    public boolean areContentsTheSame(int oldItemPosition, int newItemPosition) {        int result = newList.get(newItemPosition).compareTo(oldList.get(oldItemPosition));        return result == 0;    }        @Nullable    @Override    public Object getChangePayload(int oldItemPosition, int newItemPosition) {        ApprovedItemDetail_2 newModel = newList.get(newItemPosition);                  Bundle diff = new Bundle();                diff.putString(Constant.NAME_PUT_DIFF_FLAG,newModel.getkEYCODE());            if (diff.size() == 0) {            return null;        }                return diff;    }}