package com.firstems.erp.adapter.diff.signature;import android.os.Bundle;import androidx.annotation.Nullable;import androidx.recyclerview.widget.DiffUtil;import com.firstems.erp.api.model.response.signature.SignatureItemApiResponse;import java.util.List;public class SignatureDiffUtilCallBack extends DiffUtil.Callback {    private List<SignatureItemApiResponse> newList ;    private List<SignatureItemApiResponse> oldList;        public SignatureDiffUtilCallBack(List<SignatureItemApiResponse> newList, List<SignatureItemApiResponse> oldList) {        this.newList = newList;        this.oldList = oldList;    }        @Override    public int getOldListSize() {        return oldList != null ? oldList.size() : 0;    }        @Override    public int getNewListSize() {        return newList != null ? newList.size() : 0;    }        @Override    public boolean areItemsTheSame(int oldItemPosition, int newItemPosition) {        return newList.get(newItemPosition).getKeyCode().equals(oldList.get(oldItemPosition).getKeyCode());    }        @Override    public boolean areContentsTheSame(int oldItemPosition, int newItemPosition) {        int result = newList.get(newItemPosition).compareTo(oldList.get(oldItemPosition));        return result == 0;    }        @Nullable    @Override    public Object getChangePayload(int oldItemPosition, int newItemPosition) {        SignatureItemApiResponse newModel = newList.get(newItemPosition);        SignatureItemApiResponse oldModel = oldList.get(oldItemPosition);            System.out.println(newModel.getKeyCode());        System.out.println(oldModel.getKeyCode());                Bundle diff = new Bundle();            if (!newModel.getKeyCode().equals(oldModel.getKeyCode())) {            diff.putString("keyCode", newModel.getKeyCode());        }        if (!newModel.getValueName().equals(oldModel.getValueName())) {            diff.putString("valueName", newModel.getValueName());        }        if (newModel.getValueFld() != (oldModel.getValueFld())) {            diff.putDouble("valueField", newModel.getValueFld());        }        if (!newModel.getStatusName().equals(oldModel.getStatusName())) {            diff.putString("keyCode", newModel.getKeyCode());        }        if (!newModel.getMainCode().equals(oldModel.getMainCode())) {            diff.putString("mainCode", newModel.getMainCode());        }        if (!newModel.getMainDate().equals(oldModel.getMainDate())) {            diff.putString("mainDate", newModel.getMainDate());        }                if (diff.size() == 0) {            return null;        }        return diff;    }}