package com.firstems.erp.adapter.diff.sophieutamung;import android.os.Bundle;import androidx.annotation.Nullable;import androidx.recyclerview.widget.DiffUtil;import com.firstems.erp.api.model.response.phieu_tam_ung.PhieuTamUngItem;import com.firstems.erp.api.model.response.signature.SignatureItemApiResponse;import java.util.List;public class SoPhieuTamUngDiff extends DiffUtil.Callback {    private List<PhieuTamUngItem> newList ;    private List<PhieuTamUngItem> oldList;        public SoPhieuTamUngDiff(List<PhieuTamUngItem> newList, List<PhieuTamUngItem> oldList) {        this.newList = newList;        this.oldList = oldList;    }        @Override    public int getOldListSize() {        return oldList != null ? oldList.size() : 0;    }        @Override    public int getNewListSize() {        return newList != null ? newList.size() : 0;    }        @Override    public boolean areItemsTheSame(int oldItemPosition, int newItemPosition) {        return newList.get(newItemPosition).getiTEMNAME().equals(oldList.get(oldItemPosition).getiTEMNAME());    }        @Override    public boolean areContentsTheSame(int oldItemPosition, int newItemPosition) {        int result = newList.get(newItemPosition).compareTo(oldList.get(oldItemPosition));        return result == 0;    }        @Nullable    @Override    public Object getChangePayload(int oldItemPosition, int newItemPosition) {        PhieuTamUngItem newModel = newList.get(newItemPosition);        PhieuTamUngItem oldModel = oldList.get(oldItemPosition);            Bundle diff = new Bundle();                if (newModel.isCheck() != (oldModel.isCheck())) {            diff.putBoolean("check", newModel.isCheck());        }        return  diff;    }}