package com.firstems.erp.loading;import androidx.appcompat.app.AppCompatActivity;import android.content.Intent;import android.os.Bundle;import com.firstems.erp.R;public class FlashActivity extends AppCompatActivity {        @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_flash);        new android.os.Handler().postDelayed(                new Runnable() {                    public void run() {                        initControl();                    }                },                200);    }        private void initControl() {        Intent intent = new Intent(this, LoadingActivity.class);        startActivity(intent);        finish();    }}